<?phpif (empty($kTeach) || empty($userID)) {  echo "You are not authorized to do this!";  return;}elseif ($kTeach != $userID && $userID != ROOT_USER) {  echo "You are not authorized to do this!";  return;}#login_changepass.inc ?><div id="password_form">  <form id="password_editor" name="password_editor"        action="<?php echo site_url("auth/change_password") ?>" method="post">    <input type="hidden" name="valid_password" id="valid_password" value=false/>    <input type="hidden" name="kTeach" id="kTeach"           value="<?php echo $kTeach; ?>"/>    <?php if ($userID != ROOT_USER || $kTeach == $userID): ?>    <p><span class='notice'>You are changing your own password.</span></p>    <p><label for="current_password">Current Password: </label><br/>      <input type="password" id="current_password" name="current_password"             value=""/></p>    <p>      <?php endif; ?>      <label for="new_password">New Password: </label><br/>      <input type="password" id="new_password" name="new_password" value=""/>    </p>    <p>      <label for="check_password">Re-enter New Password: </label><br/>      <input type="password" id="check_password" name="check_password"             value=""/>    </p>    <p><span id='password_note' class='notice hidden'></span></p>    <p><input type="submit" class='button change_password hidden' id='change-password'              value='Change Password'/></p>  </form></div>