<?phpif (empty($kTeach) || empty($userID)) {  echo "You are not authorized to do this!";  return;}elseif ($kTeach != $userID && $userID != ROOT_USER) {  echo "You are not authorized to do this!";  return;}$password_for = 'Your';#login_changepass.inc ?><div id="password_form">  <form id="password_editor" name="password_editor"        action="<?php echo site_url("auth/change_password") ?>" method="post">    <input type="hidden" name="valid_password" id="valid_password" value=false/>    <input type="hidden" name="kTeach" id="kTeach"           value="<?php echo $kTeach; ?>"/>    <?php if ($userID != ROOT_USER || $kTeach == $userID): ?>    <h2>Change your password.</h2>    <p><label for="current_password">Current Password: </label><br/>      <input type="password" id="current_password" name="current_password"             value=""/></p>    <?php else: ?>    <?php $password_for = $name . '\'s'; ?>      <h2 class='warning'>You are changing <?php print $name;?>'s  password.</h2>    <label for="admin_password">YOUR Login Password: </label><br/>      <input type="password" id="current_password" name="current_password"             value=""/>    <div class="description">As an administrator, <em>your</em> password is required to make this change.</div>    <?php endif; ?>    <p>      <label for="new_password">New Password: </label><br/>      <input type="password" id="new_password" name="new_password" value=""/>    </p>    <p>      <label for="check_password">Re-enter New Password: </label><br/>      <input type="password" id="check_password" name="check_password"             value=""/>    </p>    <p><span id='password_note' class='notice hidden'></span></p>    <p><input type="submit" class='button change_password hidden' id='change-password'              value='Change Password'/></p>  </form></div>